import React, { Component } from 'react';
import { Card, CardBody, Container, Row, Button } from 'reactstrap';

export class PlainCard extends Component {
    constructor(props) {
        super(props);
        this.state = {
            english: this.props.english,
            phonetic: this.props.phonetic,
            spanish: this.props.spanish,
            audio: this.props.audio,
            id: this.props.id
        }
    }

    // playAudio(id) {
    //     // var x = document.getElementById(id);
    //     // x.play()

    //     console.log("play")
    // }

    render() {
        var audio = new Audio(this.state.audio)
        return (
            <Card>
                <CardBody>
                    <Container>
                        <Row>
                            <b>{this.state.english}</b>
                        </Row>
                        <Row>
                            {this.state.phonetic}
                        </Row>
                        <Row>
                            <i>{this.state.spanish}</i>
                        </Row>
                        {/* <audio id={this.state.id}>
                            <source src={this.state.audio} type='audio/mp3'></source>
                        </audio> */}
                        <Button onClick={() => audio.play()}>Play</Button>
                    </Container>
                </CardBody>
            </Card>
        )
    }
}
